\newcolumntype{C}[1]{>{\centering\arraybackslash}p{#1}} % center
\newcolumntype{R}[1]{>{\raggedleft\arraybackslash}p{#1}} % right
\newcolumntype{G}[1]{>{\centering\columncolor{gray!20!white}}p{#1}}
